```c
void reverseWords(char *s) {
    if(s == " ") {
        s = "";
        return;
    }
    int len = strlen(s);
    char *ret = (char *) malloc(len);
    memset(ret, '\0,', len);
    int j = 0, i = 0;
    memset(ret, '\0', len);
    char *tmp = (char *) malloc(len);
    memset(tmp, '\0', len);

    while (s[i] != '\0') {
        if (s[i] == ' ') {
            if (ret[j-1] == ' ') {
                i++;
                continue;
            }
            else {
                ret[j] = s[i];
                j++;

            }
        } else {
            ret[j] = s[i];
            j++;

        }
        i++;
    }

    int m = 0;
    int k = 0;
    char* strtmp = (char*)malloc(len+1);
    memset(strtmp, '\0', len +1);
    for (i = strlen(ret) - 1; i >= 0; i--) {

        if (ret[i] != ' ') {
            tmp[m] = ret[i];
            m++;
        } else {
            m = m-1;
            while (m >= 0 ) {
                strtmp[k] = tmp[m];
                m--;
                k++;
            }
            memset(tmp, '\0', len);
            m = 0;
            strtmp[k] = ' ';
            k++;

        }

    }
    m = m-1;
    while (m >= 0 ) {
        strtmp[k] = tmp[m];
        m--;
        k++;
    }
    memset(tmp, '\0', len);
    s = strtmp;

}
```
