```
char *simplifyPath(char *path) {
    int len = strlen(path);
    char *ret = (char *) malloc(len+1);
    memset(ret, '\0', len+1);
    char *p;
    char delim[2] = "/";
    p = strtok(path, delim);
    int i = 0, j = 0;
    while (p != NULL) {
        if (strcoll(p, ".") == 0) {
            p = strtok(NULL, delim);
            continue;
        }
        if (strcoll(p, "..") == 0) {
            while (i > 0) {
                if(ret[i] == '/') {
                    ret[i] = '\0';
                    break;
                }
                ret[i] = '\0';
                i--;
            }
            p = strtok(NULL, delim);
            continue;
        }

        if (ret[i] != '/') {
            ret[i] = '/';
            i++;
        } else {
            i++;
        }
        for (j = 0; j < strlen(p); j++) {
            ret[i] = p[j];
            i++;
        }
        p = strtok(NULL, delim);
    }
    if(strlen(ret) < 1) {
        ret[0] ='/';
    }
    return ret;

}
```

